import React, { useEffect, useState } from "react";
import axios from "axios";
import "../styles/Dishes.css";

function Dishes() {
  const [dishes, setDishes] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    axios.get("http://localhost:5000/dishes")
      .then((res) => setDishes(res.data))
      .catch((err) => console.error("Error fetching dishes:", err));
  }, []);

  const handleAddToCart = (dish) => {
    const userId = localStorage.getItem("userId");
    if (!userId) {
      alert("Please log in to add items to cart.");
      return;
    }

    axios.post("http://localhost:5000/cart/add", {
      user_id: userId,
      dish_id: dish.id,
      quantity: 1
    })
    .then(() => {
      alert(`${dish.dish_name} added to cart`);
    })
    .catch((err) => {
      console.error("Error adding to cart:", err);
      alert("Failed to add to cart");
    });
  };

  const filteredDishes = dishes.filter(dish =>
    dish.dish_name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="dishes-container">
      <h2>Explore Dishes</h2>

      <input
        type="text"
        placeholder="Search Dishes..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="search-bar"
      />

      <div className="dishes-grid">
        {filteredDishes.length > 0 ? filteredDishes.map((dish) => (
          <div key={dish.id} className="dish-card">
            <img src={dish.image_url} alt={dish.dish_name} className="dish-img" />
            <h3>{dish.dish_name}</h3>
            <p>â‚¹{dish.price}</p>
            <p className="dish-restaurant">{dish.restaurant_name}</p>
            <button className="add-to-cart-btn" onClick={() => handleAddToCart(dish)}>
              Add to Cart
            </button>
          </div>
        )) : <p className="no-result">No dishes found.</p>}
      </div>
    </div>
  );
}

export default Dishes;
