import React from "react";
import { Link, useNavigate } from "react-router-dom";
import "../styles/Navbar.css";

function Navbar({ isLoggedIn }) {
  const navigate = useNavigate();
  const restaurantId = localStorage.getItem("restaurantId");

  const handleRestaurantLogout = () => {
    localStorage.removeItem("restaurantId");
    navigate("/restaurant/login"); // redirect to restaurant login after logout
  };

  return (
    <nav className="navbar">
      <h1 className="logo">FoodieExpress</h1>
      <ul className="nav-links">
        <li><Link to="/menu">Menu</Link></li>
        <li><Link to="/restaurants">Restaurants</Link></li>
        <li><Link to="/cart">Cart</Link></li>
        {isLoggedIn && <li><Link to="/orders">Orders</Link></li>}

        <li>
      {isLoggedIn ? (
        <span onClick={logout} style={{ cursor: "pointer" }}>Logout</span>
      ) : (
        <Link to="/login">Login</Link>
      )}
    </li>

    <li>
      {isRestaurantLoggedIn ? (
        <span onClick={restaurantLogout} style={{ cursor: "pointer" }}>Restaurant Logout</span>
      ) : (
        <Link to="/restaurant/login">Restaurant Login</Link>
      )}
    </li>
      </ul>
    </nav>
  );
}

export default Navbar;
