import React, { useEffect, useState } from "react";
import axios from "axios";
import "../styles/Menu.css";

function Menu({ cart, setCart }) {
    const [menuItems, setMenuItems] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");

    useEffect(() => {
        axios.get("http://localhost:5000/menu")
            .then((res) => {
                setMenuItems(res.data);
                setLoading(false);
            })
            .catch((err) => {
                console.error("Error fetching menu:", err);
                setError("Failed to load menu");
                setLoading(false);
            });
    }, []);

    const handleAddToCart = (item) => {
        const itemWithRestaurant = { ...item, restaurant_id: item.restaurant_id };

        if (cart.length > 0 && cart[0].restaurant_id !== item.restaurant_id) {
            alert("You can order from only one restaurant at a time.");
            return;
        }

        setCart([...cart, itemWithRestaurant]);
        alert(`${item.dish_name} added to cart`);
    };

    if (loading) {
        return <div className="menu-container"><p>Loading menu...</p></div>;
    }

    if (error) {
        return <div className="menu-container"><p>{error}</p></div>;
    }

    return (
        <div className="menu-container">
            <header className="menu-header">
                <h2>All Menu Items</h2>
            </header>

            {menuItems.length === 0 ? (
                <p>No menu items available.</p>
            ) : (
                <div className="menu-grid">
                    {menuItems.map((item) => (
                        <div key={item.id} className="menu-card">
                            <img src={item.image_url} alt={item.dish_name} className="menu-img" />
                            <h3>{item.dish_name}</h3>
                            <p>â‚¹{item.price}</p>
                            <p><b>Restaurant:</b> {item.restaurant_name}</p>
                            <button className="add-btn" onClick={() => handleAddToCart(item)}>Add to Cart</button>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default Menu;
