import React, { useState } from 'react';
import axios from 'axios';

function RestaurantLogin() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const res = await axios.post("http://localhost:5000/restaurant/login", { email, password });
            localStorage.setItem("restaurantId", res.data.restaurantId);
            localStorage.setItem("restaurantName", res.data.restaurantName);
            window.location.href = "/restaurant/dashboard"; // redirect after login
        } catch (err) {
            alert("Login failed: " + err.response?.data?.message || "Unknown error");
        }
    };

    return (
        <div>
            <h2>Restaurant Login</h2>
            <form onSubmit={handleLogin}>
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <button type="submit">Login</button>
            </form>
        </div>
    );
}

export default RestaurantLogin;
