import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "../styles/Confirmation.css";

function Confirmation() {
  const location = useLocation();
  const navigate = useNavigate();
  const { deliveryPerson } = location.state || {};

  return (
    <div className="confirmation-container">
      <div className="success-icon">‚úÖ</div>
      <h2 className="confirmation-heading">Order Confirmed!</h2>
      <p className="confirmation-message">Your food is on its way! üçîüçü</p>

      {deliveryPerson ? (
        <div className="delivery-info">
          <h3>Delivery Personnel Details</h3>
          <p><strong>Name:</strong> {deliveryPerson.name}</p>
          <p><strong>Contact:</strong> {deliveryPerson.contact_info}</p>
        </div>
      ) : (
        <p className="confirmation-message">Loading delivery details...</p>
      )}

      <button className="back-home-btn" onClick={() => navigate("/")}>
        Back to Home
      </button>
    </div>
  );
}

export default Confirmation;
