import { useEffect, useState } from "react";
import { useParams } from "react-router-dom"; // Get URL parameters
import axios from "axios";

function Menu() {
    const { restaurantId } = useParams(); // Get restaurant ID from URL
    const [menu, setMenu] = useState([]);

    useEffect(() => {
        axios.get(`http://localhost:5000/menu/${restaurantId}`)
            .then(res => setMenu(res.data))
            .catch(err => console.error(err));
    }, [restaurantId]);

    return (
        <div>
            <h2>Menu</h2>
            <div className="menu-list">
                {menu.map(item => (
                    <div key={item.id} className="menu-card">
                        <img src={item.image_url} alt={item.dish_name} className="menu-img" />
                        <h3>{item.dish_name}</h3>
                        <p>â‚¹{item.price}</p>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Menu;
