import React from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Cart.css';

function Cart({ cart, setCart }) {
    const navigate = useNavigate();

    const handleRemove = (index) => {
        const updatedCart = [...cart];
        updatedCart.splice(index, 1);
        setCart(updatedCart);
    };

    const handlePlaceOrder = () => {
        if (cart.length === 0) {
            alert('Your cart is empty.');
            return;
        }

        const order = {
            user_id: 1, // You can replace this with dynamic user id if you have authentication
            restaurant_id: cart[0].restaurant_id,
            items: cart.map(item => item.dish_name),
        };

        axios.post('http://localhost:5000/place-order', order)
            .then(() => {
                alert('Order placed successfully!');
                setCart([]);
                navigate('/');
            })
            .catch((err) => {
                console.error('Order Error:', err);
                alert('Failed to place order');
            });
    };

    const totalPrice = cart.reduce((sum, item) => sum + parseFloat(item.price), 0).toFixed(2);

    return (
        <div className="cart-container">
            <h2>Your Cart</h2>
            {cart.length === 0 ? (
                <p className="empty-cart">No items in the cart.</p>
            ) : (
                <>
                    <ul className="cart-list">
                        {cart.map((item, index) => (
                            <li key={index} className="cart-item">
                                <img src={item.image_url} alt={item.dish_name} />
                                <div className="cart-info">
                                    <h3>{item.dish_name}</h3>
                                    <p>₹{item.price}</p>
                                    <button onClick={() => handleRemove(index)} className="remove-btn">Remove</button>
                                </div>
                            </li>
                        ))}
                    </ul>
                    <div className="cart-summary">
                        <h3>Total Price: ₹{totalPrice}</h3>
                        <button onClick={handlePlaceOrder} className="order-btn">Place Order</button>
                    </div>
                </>
            )}
        </div>
    );
}

export default Cart;
