import React from 'react';
import { useNavigate } from 'react-router-dom';

const Home = () => {
  const navigate = useNavigate();

  const popularDishes = [
    {
      id: 1,
      dish_name: 'Margherita Pizza',
      price: 10,
      image_url: 'https://source.unsplash.com/300x200/?pizza',
      restaurant_id: 2,
    },
    {
      id: 2,
      dish_name: 'Cheeseburger',
      price: 8,
      image_url: 'https://source.unsplash.com/300x200/?burger',
      restaurant_id: 3,
    },
    {
      id: 3,
      dish_name: 'Sushi Platter',
      price: 15,
      image_url: 'https://source.unsplash.com/300x200/?sushi',
      restaurant_id: 1,
    },
    {
      id: 4,
      dish_name: 'Pasta Alfredo',
      price: 12,
      image_url: 'https://source.unsplash.com/300x200/?pasta',
      restaurant_id: 2,
    },
  ];

  return (
    <div className="home-container">
      <div className="hero-section">
        <h1 className="main-heading">Delicious food, delivered to you</h1>
        <p className="sub-heading">Order from your favourite restaurants</p>
        <button className="explore-button" onClick={() => navigate('/restaurants')}>
          Explore Restaurants
        </button>
      </div>

      <h2 className="popular-heading" style={{ textAlign: 'center', marginTop: '40px' }}>
        Popular Picks
      </h2>

      <div
        className="popular-picks-container"
        style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}
      >
        <div
          className="popular-dishes-grid"
          style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(180px, 1fr))',
            gap: '1rem',
            maxWidth: '1000px',
          }}
        >
          {popularDishes.map((dish) => (
            <div
              key={dish.id}
              className="dish-card"
              style={{
                border: '1px solid #ddd',
                borderRadius: '8px',
                padding: '10px',
                textAlign: 'center',
                cursor: 'pointer',
                transition: 'transform 0.2s',
              }}
              onClick={() => navigate(`/dishes?restaurant_id=${dish.restaurant_id}`)}
              onMouseEnter={(e) => (e.currentTarget.style.transform = 'scale(1.05)')}
              onMouseLeave={(e) => (e.currentTarget.style.transform = 'scale(1)')}
            >
              <img
                src={dish.image_url}
                alt={dish.dish_name}
                style={{ width: '100%', height: '150px', borderRadius: '8px', objectFit: 'cover' }}
              />
              <h3 style={{ marginTop: '10px' }}>{dish.dish_name}</h3>
              <p>${dish.price}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Home;
