import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Home.css';

function Home() {
  const navigate = useNavigate();

  return (
    <div className="home-container">
      <section className="hero">
        <h1>Your Favourite Meals, Delivered Fast!</h1>
        <button onClick={() => navigate('/restaurants')}>Explore Restaurants</button>
      </section>

      <section className="features">
        <div className="feature-card">üöÄ Fast Delivery</div>
        <div className="feature-card">ü•ó Fresh Food</div>
        <div className="feature-card">üîê Secure Payment</div>
      </section>

      <section className="popular">
        <h2>Popular Restaurants</h2>
        {/* Horizontal scroll of restaurants here */}
      </section>

      <footer>
        <p>&copy; 2025 FoodExpress. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default Home;
